import React, { useEffect } from 'react'
import { useDispatch,useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { addToCart } from '../redux/actions/cartAction'
import Loading from './Loading'
import MassageBox from './MassageBox'



const Cart = ({location, match}) => {

    const  productId  = match.params.id;
    const qty = location.search ? Number(location.search.split('=')[1]) : 1;

    const dispatch = useDispatch()
    
    const cart = useSelector(state=>state.cart)
    const { cartItems } = cart;

    useEffect(()=> {

        if(productId)
        dispatch(addToCart(productId,qty))
    },[dispatch,qty,productId])
    return (
        <div className='grid  '>
        <div className='md:grid-rows-4'>
        <h1 className='text-center font-medium text-4xl text-blue-500 m-6'>shopping cart</h1>
        <div className='w-3/4 h-auto bg-gray-300'>
        {
            cartItems.length ===0 ? <MassageBox className='pl-8 text-2xl font-semibold '>Your cart is empty<Link to='/' className='text-lg  float-right underline text-blue-400 p-5'>go back</Link></MassageBox> :
            <div>{}</div>
        }
        </div>
        </div>
        <div></div>
            
        </div>
    )
}

export default Cart
