import { Link, useHistory } from 'react-router-dom'
import { detailsProduct } from '../redux/actions/ProductAction'
import { useDispatch, useSelector } from "react-redux";
import React, { useEffect } from 'react'
import Loading from '../components/Loading';
import MessageBox from '../components/MassageBox';

const ProductScreen = ({ match }) => {

    const history = useHistory()
     const handleDir = ()=>history.push('/cart')

    const dispatch = useDispatch();
    const productId = match.params.id
    const productDetails = useSelector((state => state.productDetails));
    const { loading, error, product } = productDetails;

    useEffect(() => {
        dispatch(detailsProduct(productId))
    }, [dispatch, productId])

    return (
        <section className='h-screen mt-16 overflow-hidden '>
            <Link to='/' className='text-lg text-gray-500 p-12  ' >go back</Link >
            {loading ? (<Loading />) : error ? (<MessageBox>{error}</MessageBox>)
                : <div className=' m-auto flex items-start justify-center flex-auto border-b-2 pb-9'>
                    <div className='h-96 w-2/4   max-w-lg'>
                        <img src={product?.image} alt={product?.name} className='pb-6 h-full w-full' />
                    </div>
                    <div className=' flex-grow px-8  max-w-xs '>
                        <ul className='border-l-2 pl-9'>
                            <li className='text-gray-800 font-bold'>{product?.name}</li>
                            <li className='py-5'>{product?.description}</li>
                            <li>{product?.rating}</li>

                        </ul>
                    </div>
                    <div className=' border-l-2 pl-9'>
                        <ul>
                            <li><span className='font-bold text-gray-800'>price : R </span>{product?.price}</li>
                            <li><div>status</div>
                                <div className='py-4'>{product?.countInStock > 0 ? <span className='text-green-400'>{product?.countInStock} items still available</span> : <span className='text-red-400'>we are out of stock</span>}</div>
                            </li>
                            <button  
                            className='focus:outline-none w-full bg-green-200 rounded-xl text-sm p-2 text-gray-500' 
                            onClick={handleDir}
                            >add to cart</button>
                        </ul>
                    </div>
                </div>
            }

        </section>
    )
}

export default ProductScreen
