import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { FaTrash } from 'react-icons/fa'
import { addToCart } from '../redux/actions/cartAction'
import MassageBox from './MassageBox'

const Cart = () => {
 

  const dispatch = useDispatch()

  const cart = useSelector((state) => state.cart)
  const { cartItems } = cart

    return (
      <div className='min-h-full'>
      <h1 className='mt-14 text-blue-500 text-center font-semibold text-4xl'>shopping cart</h1>
      <div>
      {
        cartItems.length ===0? <span className='block mt-40 text-center text-lg text-red-300'>your cart is empty <Link className='block mt-6 mb-24 text-gray-700 text-2xl hover:text-purple-300' to='/'>go Shopping</Link></span> : (<div className='min-h-full mb-56 mt-8  bg-gray-200'>{
          cartItems.map(item=>(<div className='w-full h-36 my-auto mx-8  flex justify-start  items-start' key={item.product}>
          <div className='h-full w-36'>
          <img className='h-full  w-full' src={item.image} alt={item.name}/>
          </div>
          <div className='mx-7 pt-3'><Link to={`/product/${item.product}`}>{item.name}</Link></div>
          <div className='mx-7 pt-3' ><Link>R {item.price}</Link></div>
          <div  className='mx-7 pt-3'><Link>
           <form className='border-blue-200 border w-full bg-gray-300 mb-2 text-gray-400 rounded-md'>
                           <select className='outline-none border-none w-3/4   text-gray-500'  value={item.qty}
                      onChange={(e) =>
                        dispatch(
                          addToCart(item.product, Number(e.target.value))
                        )
                      }>{
                              [...Array(item.countInStock).keys()].map(x=>(<option className='outline-none border-none w3/4 text-blue-3' key={x +1} value={x +1 }>{x + 1}</option>))
                           }
                           </select>
                            </form> 
          </Link></div>
          <button className='mx-8 pt-4 text-purple-300 text-lg hover:text-gray-300' type='button' ><FaTrash/></button>

          </div>))
        }</div>)
      }
      </div>
      <div></div>
      <div></div>
      </div>
   
  )
  
  }

 


export default Cart
