import React, { useEffect } from 'react'
import { useDispatch,useSelector } from 'react-redux'
import { useLocation, useParams ,Link} from 'react-router-dom'
import { addToCart } from '../redux/actions/cartAction'
import Loading from './Loading'
import MassageBox from './MassageBox'



const Cart = () => {

    const { id } = useParams();
    const productId = id;
    const location = useLocation();
    const qty = location.search ? Number(location.search.split('=')[1]) : 1

    const dispatch = useDispatch()
    
    const cart = useSelector(state=>state.cart)
    const { cartItems } = cart;

    console.log(cartItems)
    useEffect(()=> {

        if(productId)
        dispatch(addToCart(productId,qty))
    },[dispatch,qty,productId])
    return (
        <div className='grid  '>
        <div className='md:grid-rows-4'>
        <h1 className='text-center font-medium text-lg'>shopping cart</h1>
        {
            cartItems.length ===0 ? <h3>Your cart is empty<Link to='/'>go back</Link></h3> :
            <div>{cartItems.map(item=>(<h3 key={item.product}>{item.image}</h3>))}</div>
        }
        </div>
        <div></div>
            
        </div>
    )
}

export default Cart
