import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'

import { addToCart } from '../redux/actions/cartAction'
import MassageBox from './MassageBox'

const Cart = () => {
 

  const dispatch = useDispatch()

  const cart = useSelector((state) => state.cart)
  const { cartItems } = cart

    return (
      <div className='min-h-full'>
      <h1 className='mt-14 text-blue-500 text-center font-semibold text-4xl'>shopping cart</h1>
      <div>
      {
        cartItems.length ===0? <span className='block mt-40 text-center text-lg text-red-300'>your cart is empty <Link className='block mt-6 mb-24 text-gray-700 text-2xl hover:text-purple-300' to='/'>go Shopping</Link></span> : (<div>{
          cartItems.map(item=>(<div key={item.product}>
          <img src={item.image} alt={item.name}/>
          </div>))
        }</div>)
      }
      </div>
      <div></div>
      <div></div>
      </div>
   
  )
  
  }

 


export default Cart
